mapper.select:
select count(1)
FROM
t_ebm_producer tep
LEFT JOIN t_dictionary_type t ON tep.DICT_TYPE = t.ID
LEFT JOIN t_dictionary_data d ON t.ID = d.DICT_TYPE
WHERE t.ACTIVE ='Y' AND tep.MSG_TYPE = d.VALUE_ORDER

    AND d.VALUE_ORDER=#{msgType}


    AND MSG_HOST=#{msgHost}


    AND MSG_PORT=#{msgPort}


    AND QUEUE_CHANNEL=#{queueChannel}


    AND ESBCODE=#{esbcode}


    AND QUEUE_MANAGER=#{queueManager}

SELECT t.TEP_ID, tdd.VALUE_NAME MSG_TYPE, #t.MSG_TYPE, t.DICT_TYPE, t.MSG_HOST, t.MSG_PORT, t.QUEUE_CHANNEL, t.QUEUE_MANAGER, t.CCSID, t.ESBCODE, t.REQUEST_COMOUT, t.SLEEP_TIME, t.MSG_COUNT, t.MSG_SUCCESS_COUNT, t.THREAD_COUNT, t.MESSAGE_SIZE, t.PRODUCE_STATUS, t.PRODUCE_START_TIME, t.PRODUCE_END_TIME, t.LAST_PRODUCE_TIME, t.CREATE_TIME, t.UPDATE_TIME FROM t_ebm_producer t LEFT JOIN t_dictionary_data tdd ON t.DICT_TYPE = tdd.DICT_TYPE AND t.MSG_TYPE = tdd.VALUE_ORDER WHERE 1=1
and t.ESBCODE LIKE CONCAT('%',#{esbCode},'%')
AND t.PRODUCE_START_TIME >= #{produceStartTime}
AND t.PRODUCE_END_TIME <= #{produceEndTime}
ORDER BY t.UPDATE_TIME DESC

SELECT t1.* FROM t_ebm_producer t1 WHERE t1.TEP_ID IN
#{eBMProduceId}
ORDER BY t1.TEP_ID
