mapper.select:
SELECT t1.* FROM t_second_basic t1 WHERE t1.SECOND_LINK_ID IN
#{secondLinkId}
ORDER BY t1.SECOND_LINK_ID

SELECT count(DISTINCT tsb.BLINK_BANK)
FROM t_second_basic tsb
WHERE tsb.SECOND_LINK_ID = #{secondLinkId}

SELECT tslm.LINK_ID SECOND_LINK_ID, tslm.LINK_NAME SECOND_LINK_NAME, tsb.BASIC_LINK_ID LINK_ID, tlm.LINK_NAME LINK_NAME, tsb.BLINK_ORDER, tsb.BLINK_BANK FROM t_second_basic tsb, t_second_link_mnt tslm, t_link_mnt tlm WHERE tsb.SECOND_LINK_ID=tslm.LINK_ID and tsb.BASIC_LINK_ID=tlm.LINK_ID
and tslm.LINK_ID=#{secondLinkId}
order by tsb.BLINK_BANK, tsb.BLINK_ORDER
