mapper.insert:

insert into t_shadow_table_config (ID, SHADOW_DATASOURCE_ID, SHADOW_TABLE_NAME,
ENABLE_STATUS, CREATE_TIME, UPDATE_TIME
)
values (#{id,jdbcType=BIGINT}, #{shadowDatasourceId,jdbcType=BIGINT}, #{shadowTableName,jdbcType=VARCHAR},
#{enableStatus,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
)

mapper.delete:
delete from t_shadow_table_config
where ID in

    #{id}

delete from t_shadow_table_config where SHADOW_DATASOURCE_ID in (
select SHADOW_DATASOURCE_ID from t_shadow_table_datasource where APPLICATION_ID in

    #{applicationId}

)

mapper.select:
SELECT
FROM t_shadow_table_config WHERE SHADOW_DATASOURCE_ID = #{shadowDatasourceId} AND SHADOW_TABLE_NAME IN
#{shadowTableName}
