mapper.select:
SELECT DISTINCT DATABASE_IPPORT FROM t_shadow_table_datasource WHERE APPLICATION_ID = #{applicationId}
AND USE_SHADOW_TABLE = 1

    AND BINARY DATABASE_NAME = #{dbName}

SELECT SHADOW_DATASOURCE_ID, APPLICATION_ID, DATABASE_IPPORT, DATABASE_NAME
FROM T_SHADOW_TABLE_DATASOURCE
WHERE APPLICATION_ID = #{applicationId}

select SHADOW_DATASOURCE_ID
from t_shadow_table_datasource
where APPLICATION_ID = #{applicationId,jdbcType=BIGINT}
  and DATABASE_NAME = #{databaseName}
  and DATABASE_IPPORT = #{databaseIpPort}

SELECT DISTINCT t2.APPLICATION_ID applicationId, t2.APPLICATION_NAME applicationName FROM t_shadow_table_datasource t1 left join t_application_mnt t2 on t1.APPLICATION_ID = t2.APPLICATION_ID where t2.APPLICATION_ID is not null
and t1.USE_SHADOW_TABLE = #{useShadowTable}
order by 2
